pragma solidity ^0.8.0;

contract SensorData {
    struct Measurement {
        uint humidityAir;
        uint humiditySoil;
        uint temperature;
    }

    mapping(uint => Measurement) public measurements;
    uint public measurementIndex = 0;

    function addMeasurement(
        uint _humidityAir,
        uint _humiditySoil,
        uint _temperature
    ) public {
        Measurement memory newMeasurement = Measurement(
            _humidityAir,
            _humiditySoil,
            _temperature
        );
        measurements[measurementIndex] = newMeasurement;
        measurementIndex++;
    }
}
